import { createGlobalStyle } from 'styled-components'

export const GlobalStyles = createGlobalStyle`

background:black;
  background-image:url("https://i2.wp.com/www.handsomephantom.com/wp-content/uploads/2017/07/Space-Wallpapers-HD-Wallpapers-Art-Prints-Posters-Desktop.jpg?fit=2048%2C1130&ssl=1");
  background-repeat: no-repeat;
  background-repeat: no-repeat; 
  background-size: cover; 
  animation: darkrise 200s; 
  animation-duration: 200s;
 transition: all 200s ease;
 animation-iteration-count:infinite;

  }
`
