import styled from 'styled-components';
export const StyledIntro = styled.div`
position: relative;
height: 100vh;
background : black;
.line{
   color:red; 
   width: 100%;
   diplay:flex;
   white-space: nowrap;
   position: absolute;
  top: 50%;

  font-weight:900;
  transform: translate(-50%, -50%);
  -ms-transform: translate(-50%, -50%);
  animation: animateline 4s linear;
}
@keyframes animateline
{
  0%
  {
    top: 60%;

    transform: translatex(100%)
  }
  100%
  {
    top: 60%;

    transform: translatex(-100%)
  }
}
}


`;
