import { StyledCover } from './Cover.styled';
import Main from '../Main';
import React, { Component } from 'react';
import { MDBBtn} from 'mdbreact';
import ReactTextTransition, { presets } from "react-text-transition";
import {  CSSTransition }from 'react-transition-group';
import ReactPlayer from 'react-player'
import styled, { keyframes } from 'styled-components'



const hossik = ["HOSSIK PAGE"," "]
const welcome = ["WELCOME TO"," "]

export class Cover extends Component {

    constructor(props) {
        super(props);
        this.switchImage = this.switchImage.bind(this);
        this.pause = this.pause.bind(this);
        const video = keyframes`
        0% { opacity: 1.0; }
        70% { opacity: 1.0; }
        80% { opacity: 0.3; }
        90% { opacity: 1.0; }
        `;
        const Video = styled.div`
          animation: ${video} 9s linear infinite;

        `;
        this.state = {
          currentImage: 0,
          play:false,
          back:false,
          textIndex: 0,
          titletime :false,
          player : false,
          helpplayer:false,
          duration: 0,
          images: [
            "https://cdn.videvo.net/videvo_files/video/free/2019-07/small_watermarked/190625_04_CityMisc_HD_15_preview.webm",
            "https://cdn.videvo.net/videvo_files/video/free/2019-07/small_watermarked/190625_26_RainyStreets_HD_29_preview.webm",
            "https://www.videvo.net/videvo_files/converted/2018_04/preview/180301_05_A_CBDCommuters_01.mp415835.webm?v=1740601"

          ]
        };
      }
      remove = () => { 
        clearInterval(this.intervalID)
      }
      removetitle = () => { 
        clearInterval(this.intervalTITLE)
      }
      removertitle = () => { 
        clearInterval(this.intervalRTITLE)
      }
      removeplayer = () => {
        clearInterval(this.intervalPlayer)
      }

    componentDidMount() {

        this.intervalID = setInterval(() => {
        this.setState({
            textIndex: this.state.textIndex + 1,
        });
        }, 9900);
        this.intervalTITLE = setInterval(() => {this.setState({
          titletime: true
        }); }, 2000);
        this.intervalRTITLE = setInterval(() => {this.setState({
          titletime: false
        }); }, 8000);
        this.intervalPlayer = setInterval(() => {this.setState({
          player: true
        }); }, 10000);
        this.intervalHelpPlayer = setInterval(() => {this.setState({
          helpplayer: true
        }); }, 12000);

        setInterval (this.remove);
        setInterval (this.removetitle, 6000);
        setInterval (this.removertitle, 17000);

    }





      switchImage() {

        if (this.state.currentImage < this.state.images.length - 1 ) {
          this.setState({
            currentImage: this.state.currentImage + 1,
          })
        } else {
          this.setState({
            currentImage: 0
          });
        }
        return this.currentImage;
      }


    pause = () => {
  this.setState(state => ({
    play: !state.play
  }))
  }

  changeStyle = () => {
    if(this.state.videoStyel === true){
    this.setState ({
      videoStyel: false
    })}
  }
  handleDuration = (duration) => {

    this.setState({ duration })
  }



    render() {

      console.log('onDuration', this.state.duration)
        return (

        <StyledCover>

        {this.state.play ? 
        <Video>
        <div className='player-wrapper'>
        <ReactPlayer
          playing	="true"
          className='react-player'
          onError={this.switchImage}
          onEnded={this.switchImage}
          url={this.state.images[this.state.currentImage]}
          width='100%'
          height='100vh'
          onDuration={this.handleDuration}
        />
      </div>
      </Video>
      : 
      <img src={"https://raw.githubusercontent.com/Hossik/Profilio/master/public/assets/photo390.png"} alt="img"/> }
        <div className="dark-overlay ">
        <div className="scroll">
          <div id="welcome">
            Wolcome-{'    '}<span>{'    '}To-</span>{'    '}<span>{'    '}My-{'    '}</span>{'    '}<span>{'    '}Page{'    '}</span>
            <span className="first">Wolcome-{'    '}</span><span className="first">{'    '}To-</span>{'    '}<span className="first">{'    '}My-{'    '}</span>{'    '}<span className="first">{'    '}Page{'    '}</span>
            <span>Wolcome-{'    '}</span><span>{'    '}To-</span>{'    '}<span>{'    '}My-{'    '}</span>{'    '}<span>{'    '}Page{'    '}</span>
          </div>
        </div>
        <CSSTransition
        in={this.state.player}
        timeout={300}
        classNames="alertup"
        unmountOnExit
        appear     
      >
        <div id="controler">
        <MDBBtn id="play" color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}
      onClick={this.pause}>{this.state.play?<i id="nohossik" class="fas fa-pause-circle"></i>:<i id="hossik" class="fas fa-play-circle"></i>}</MDBBtn> { }
      <MDBBtn id="back" color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}onClick={this.backward}>{this.state.back?<i id="nohossik" class="fas fa-backward"></i>:<i id="hossik"  class="fas fa-forward"></i>}</MDBBtn></div>
      </CSSTransition>
      <CSSTransition
        in={this.state.helpplayer}
        timeout={300}
        classNames="helpplayer"
        unmountOnExit
        appear     
      >
      <div id="help" style={{  display : this.state.play ?  "none"  : "block" }}>
        <span><i class="fas fa-angle-double-up"></i></span>
        <p> you can play background slides here </p>
      </div>
      </CSSTransition>
        <div className ="landing-inner">

        <div className='inner'>

            <Main className="hoss"/>



            <CSSTransition
        in={this.state.titletime}
        timeout={300}
        classNames="alertup"
        unmountOnExit
        appear     
      >
            <section  id= "up" className="linetext">
            <ReactTextTransition
               text= { welcome[this.state.textIndex % welcome.length] }
               springConfig={ presets.molasses
               }
               delay={400}
               className="big"
               direction="up"
               inline
            />
            </section>
            </CSSTransition>
            <CSSTransition
        in={this.state.titletime}
        timeout={300}
        classNames="alertdown"
        unmountOnExit
        appear     
      >
            <section id="down" className="linetext">
            <ReactTextTransition
               text= { hossik[this.state.textIndex % hossik.length] }
               springConfig={ presets.molasses
               }
               delay={400}
               className="big"
               direction="down"
               inline
            />

            </section>
            </CSSTransition> 
        </div>
        </div>
        </div>
        </StyledCover>)


    }
}

export default Cover
