import React, { Component } from 'react';
import Info from './Info'
export class Header extends Component {
    constructor(props) {	 
        super(props);
        this.state = {	
            click: null	
        }	
    }	

    onclick =(click) =>{
        this.setState({click: click})
    }
    render() {
        const button = 

            <div className="header">
                <button type="button" class="btn btn-dark btn-circle btn-xl" onClick= {() =>this.onclick(1)}><i class="fa fa-question"></i></button> 
            </div> 


        return (
            <div className="container">
            {button}
            <div className="info col-12 ">
            <Info click={this.state.click} />
            </div>
            </div>
        )
    }
}

export default Header
