import React, { Component } from 'react'
import { Switch, Route, Redirect, Link } from 'react-router-dom';
import { MDBBtn, MDBCollapse } from 'mdbreact';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle }
from 'reactstrap';
import {BrowserRouter, withRouter} from 'react-router-dom';
import { Contacts } from './Contacts'
import { TransitionGroup, CSSTransition }from 'react-transition-group'
import Typist from 'react-typist';
import {Wave} from "react-animated-text";
import ReactTextTransition, { presets } from "react-text-transition";
const texts = ["Click", "or", "Die"]

class Main extends Component {
  state = {
    collapseID: "",
    textIndex: 0,
    textFastIndex: 0
    };

  toggleCollapse = collapseID => () => {
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  }
  componentDidMount() {
    setInterval(() => {
      this.setState({
        number: randomNumber(),
        textIndex: this.state.textIndex + 1,
        paragraphIndex: this.state.paragraphIndex + 1
      });
    }, 4000);
    setInterval(() => {
      this.setState({
        textFastIndex: this.state.textFastIndex + 1
      });
    }, 150);
  }
  render() {

    const Home = () => {
      return (
        <div>
          <CardImg top id="cardimage"  width="100%" src="../assets/hossik.jpeg" alt="hossik" />
          <CardBody>
            <CardSubtitle >
            <Typist> 
              Hello, My Name is Hossik
            </Typist>   
            </CardSubtitle>
            <CardText id="cardtext" className='row justify-content-center'>
            <div className="textCard">
        <section className="inline">
        <Typist>
        <Typist.Delay ms={2000} />
        <br />
        Quis commodo velit quis cupidatat non amet aliqua sint veniam labore.<Typist.Delay ms={1000} />
        <br />
         Culpa ea laboris consequat mollit proident Lorem cupidatat officia fugiat. <Typist.Delay ms={1000} />
        <br />Tempor sunt non ullamco irure in proident.<Typist.Delay ms={1000} />
        <br /> Laboris minim esse qui non id et anim commodo. <Typist.Delay ms={1000} />
        <br />Esse id do irure ut eiusmod aliqua irure dolor in est ullamco culpa enim.

        </Typist>
        <Wave 
          text="_"
          effect="verticalFadeIn"
          effectChange={0}
          effectDirection="down"
          effectDuration={0.1}
          inline
        />
        </section>
      </div>
          </CardText>
          </CardBody>
        </div>
      )
    }
    const Tools = () => {
      return (
        <div>
          <CardImg top id="cardimage" width="100%" src="../assets/react.png" alt="hossik" />
          <CardBody>
            <CardSubtitle className='row justify-content-center'>Hello, My Name is Hossik</CardSubtitle>
            <CardText id="cardtext">I am a Designer / Front End Developer living in Kyiv, Ukraine.
          </CardText>
          </CardBody>
        </div>
      )
    }


  return (

    <React.Fragment className="header col-12 col-md-10 ">
        <section>
        <section className="inline" id="hossik">
          <ReactTextTransition
            text={texts[this.state.textIndex % texts.length]}
            spring={presets.gentle}
            className="big"
            delay={300}
            inline
          />   
        </section>
      </section>
      <MDBBtn
        className="btn btn-dark btn-circle btn-xl"
        onClick={this.toggleCollapse("basicCollapse")}>
        <i class="fa fa-cog fa-spin fa-3x fa-fw"></i>    </MDBBtn>
      <MDBCollapse id="basicCollapse" isOpen={this.state.collapseID}>
        <div>
          <Card id="card" className="col-12 col-md-10" style={{width:'100rem' ,height:"60rem"}}>
            <CardTitle id="cardtitle" className='row justify-content-center'>
              <Link className="links" to="/home"><span>home</span></Link>      
              <Link className="links" to="/tools"><span>tools</span></Link><Link className="links" to="/contacts"><span>contacts</span></Link>
            </CardTitle>
            <TransitionGroup id="components">
              <CSSTransition key={this.props.location.key} classNames="fade" timeout={{ enter: 300, exit: 300 }}>
              <section className="route-section">
            <Switch location={this.props.location}>
              <Route path="/home" component={Home} />
              <Route path="/tools" component={Tools} />
              <BrowserRouter>
                <div className='hoss '>
                  <Contacts />
                </div>
              </BrowserRouter>        
              <Redirect to="/home" />
            </Switch>
            </section>
            </CSSTransition>
            </TransitionGroup>
          </Card>
        </div>
      </MDBCollapse>
    </React.Fragment>
    )
  }
}

export default withRouter(Main);
