          return (
            <div className="info ">
            <Card>
            <CardImg src='../assets/hoss.jpeg' alt='photo' />
            <CardBody>
              <CardTitle className='row justify-content-center'>I am a web developer</CardTitle>
            </CardBody>
          </Card>
          </div>
          )
        }
    render() {
        console.log (this.props.click)
        return (
            <div className='col-12' >
               <div>{this.renderInfo(this.props.click)}</div> 
            </div>
        )
    }
}

export default Info
