import React, { Component } from 'react'
import Info from './Info'
import Header from './Header'
import Hoss from './Hoss'
import Home from './Home'
import Face from './Face'
import { Switch, Route, Redirect } from 'react-router-dom';
import { MDBCard,MDBCardTitle, MDBCardText,MDBCardImage,MDBCardBody} from 'mdbreact';

export class Main extends Component {
  toggleButton(){
    this.setState({
      isButtonOpen: !this.state.isButtonOpen
    });
  }

render() {
     const Form = () =>{ 
      return(
     <div className=" col-12 col-md-10 ">

       <button type="button" onClick={this.toggleButton} class="btn btn-dark btn-circle btn-xl" > form</button>


     <MDBCard style={{ width: "22rem" }}>
     <MDBCardImage className="img-fluid" src='../assets/hoss.jpeg' waves />
     <MDBCardBody>
    <MDBCardTitle  className='row justify-content-center'>Hoss</MDBCardTitle>
    <MDBCardText style={{ fontSize: "2rem" }} className='row justify-content-center' >
    I am a web developer
    </MDBCardText>
    </MDBCardBody>
    </MDBCard>
    </div>)

    }

  return (
    <div >
      <Form />
      <Header />
      <Switch>
      <Route  path="/info" component={Info} />
      <Route path="/home" component={Home} />
      <Route path= "/face" component={Face} />
      <Redirect to="/hoss" component={Hoss} />

      </Switch>
      <Hoss />
    </div>
  )
}
}

export default Main
