import { createGlobalStyle } from 'styled-components'

export const GlobalStyles = createGlobalStyle`
  body {
    background: -moz-linear-gradient(45deg, rgba(233, 25, 25, 0.6), rgba(255, 220, 0, 0.69) 100%) ;
    background: -webkit-linear-gradient(45deg, rgba(233, 25, 25, 0.6), rgba(255, 220, 0, 0.69) 100%);
    background: linear-gradient(45deg, rgba(233, 25, 25, 0.6), rgba(255, 220, 0, 0.69) 100%);



  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;


//   -webkit-backface-visibility: hidden;
// 	-webkit-transform: scale(1);


    }
  }

`
