import { StyledIntro } from './Intro.styled';
import React, { Component } from 'react';
import ReactTextTransition, { presets } from "react-text-transition";

const hossik = ["HOSSIK PAGE"," "]
const welcome = ["WELCOME TO"," "]
export class Intro extends Component {
        state = {
            textIndex: 0
        };

        remove = () => {
            clearInterval(this.intervalID)
          }
        componentDidMount() {

            this.intervalID = setInterval(() => {
            this.setState({
                textIndex: this.state.textIndex + 1,
            });
            }, 2000);

            setInterval (this.remove)

        }
    render(){
        return(
        <StyledIntro>
        <section className="textline" id= "hossik" >
            <ReactTextTransition
               text= { welcome[this.state.textIndex % welcome.length] }
               spring={presets.gentle}
               delay={300}
               className="big"
               direction="up"
               inline
            />
            </section>

        <section className="textline" id="nohossik">
            <ReactTextTransition
               text= { hossik[this.state.textIndex % hossik.length] }
               springConfig={ presets.wobbly }
               delay={300}
               spring={presets.gentle}
               className="big"
               direction="down"
               inline
            />
            </section>
            </StyledIntro>
        )
    }
}
export default Intro
