import React from 'react';
import { MDBIcon, MDBBtn,MDBCard,MDBCardTitle, MDBCardText,MDBCardImage,MDBCardBody} from 'mdbreact';
function RenderInfo ({info}) {

if (info != null)
return (
  <div className=" col-12 col-md-10 m-1 ">
        <MDBCard style={{ width: "22rem" }}>
        <MDBCardImage className="img-fluid" src='../assets/hoss.jpeg' waves />
        <MDBCardBody>
          <MDBCardTitle>Hoss</MDBCardTitle>
          <MDBCardText justify-content-center >
          I am a web developer.
          </MDBCardText>
          <MDBBtn floating social="tw" size="lg">
        <MDBIcon fab icon="twitter" className="pr-1" />
      </MDBBtn>
        </MDBCardBody>
      </MDBCard>
</div>
)
}

const Info = (props) =>{
if (props.click != null)
return (
<div className='container'>
  <div className='align-self-center  '>
    <RenderInfo info = {props.click}/>
  </div>
</div>
)
else{
  return <div></div>;
}
}

export default Info
