import React, { Component } from 'react';
import { CardBody,CardFooter } from 'reactstrap';
import Typist from 'react-typist';
import { Link } from 'react-router-dom';


export class AboutMe extends Component {
  state ={
    blinking : "|" 
  }
  blink = () => {
    this.setState({
      blinking :"_"
    })
  } 
  blinkFinish = () => {
    this.setState({
      blinking :"|"
    })
  } 
  render(){
  return (
      <div>
    <div>     
      <CardBody id="zir">

          <Typist  
          className="textCard"
          stdTypingDelay="100" 
          startDelay="2500"
          cursor={{
            show: true,
            blink: true,
            element: this.state.blinking,
            hideWhenDone: true,
            hideWhenDoneDelay: '1000',
          }}
          onCharacterTyped={this.blink}
          onLineTyped={this.blinkFinish}
          onTypingDone={this.blinkFinish}
          >
            <div style={{fontSize:"4rem"}}>
            Its Me</div><br/>
          Typist will animate any text present in its descendents. Each text node will be animated<Typist.Delay ms={2500} /><Typist.Backspace count={8} delay={200} /><br/><Typist.Delay ms={2500} />as it is encountered in depth-first traversal of the children tree, one after the other.

          </Typist>

      </CardBody>

    </div>
    <CardFooter className="footer">
            <Link className="links" to="/facebook"><span>facebook</span></Link>
            <Link className="links" to="/linkedin"><span>linkedin</span></Link>
            <Link className="links" to="/github"><span>github</span></Link>
          </CardFooter>
          </div>
    )
  }
}
export default AboutMe;
