import { createGlobalStyle } from 'styled-components'

export const GlobalStyle = createGlobalStyle`
  body {

background:yellow;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  height: 1800px;
width :90%;

  -webkit-backface-visibility: hidden;
	-webkit-transform: scale(1);
  -webkit-transition: background-image 0.2s ease-in-out;
  -webkit-transition: all 4s ease, z-index 1ms;
       -moz-transition: all 4s ease, z-index 1ms;
        -ms-transition: all 4s ease, z-index 1ms;
         -o-transition: all 4s ease, z-index 1ms;
            transition: all 4s ease, z-index 1ms;
  transition: background-image 0.2s ease-in-out;


    animation-name: blinker;

    animation-duration: 10s;
    animation-timing-function: ease-in-out;
    animation-iteration-count: infinite;
    @keyframes blinker {
      0% {background-image : url(${({ theme }) => theme.color_1})}
