// import React from 'react';
import { StyledCover } from './Cover.styled';
import Main from '../Main';
import React, { Component } from 'react'

export class Cover extends Component {

    constructor(props) {
        super(props);
        this.switchImage = this.switchImage.bind(this);
        this.state = {
          currentImage: 0,
          images: [
  '../assets/Screenshot21.png', 
  '../assets/Screenshot21.png', 
  '../assets/Screenshot23.png', 
  '../assets/Screenshot24.png', 
  '../assets/Screenshot25.png', 
  '../assets/Screenshot26.png', 
  '../assets/Screenshot27.png', 
  '../assets/Screenshot29.png', 
  '../assets/Screenshot31.png', 
   '../assets/Screenshot32.png', 
   '../assets/Screenshot34.png', 
   '../assets/Screenshot36.png', 
   '../assets/Screenshot38.png', 
   '../assets/Screenshot39.png', 
   '../assets/Screenshot40.png', 
   '../assets/Screenshot42.png', 
   '../assets/Screenshot43.png',
   '../assets/Screenshot44.png', 
   '../assets/Screenshot45.png', 
   '../assets/Screenshot47.png',
          ]
        };
      }

      switchImage() {
        if (this.state.currentImage < this.state.images.length - 1) {
          this.setState({
            currentImage: this.state.currentImage + 1
          });
        } else {
          this.setState({
            currentImage: 0
          });
        }
        return this.currentImage;
      }

      componentDidMount() {
        setInterval(this.switchImage, 1000);
      }


    render() {
        return (

            <StyledCover>
        <img src={this.state.images[this.state.currentImage]} alt="img" />
        <div className ="landing-inner">
        <div className='inner'>
            <Main className="hoss"/>
        </div>
        </div>
        </StyledCover>

        )
    }
}

export default Cover











