import { createGlobalStyle } from 'styled-components'

export const GlobalStyle = createGlobalStyle`
  body {
    background-color: ${props => (props.whiteColor ? 'white' : 'red')};
  }
  .hoss{
    font-family: 'Changa', sans-serif;
    align-items: center;
    justify-content: center;
    margin-top: 100px;
  }
`
