import styled from 'styled-components';
export const StyledIntro = styled.div`
height: 100vh;
width: 100%;
background : black;
position: fixed;
.linetext{
    margin-top: 45vh;
    margin-left: 50vw;
    font-family: 'Changa', sans-serif;
    align-items: center;
    justify-content: center;
    text-align: center;

}
.down{

    margin-top: 50vh;
    margin-left: 50vw;
}

.line{
   color:white; 
   width: 100%;
   diplay:flex;
   white-space: nowrap;
   position: absolute;
  top: 50vh;

  font-weight:900;
  transform: translate(-50%, -50%);
  -ms-transform: translate(-50%, -50%);
  animation: animateline 4s linear;
}
@keyframes animateline
{
  0%
  {
    top: 48.65vh;

    transform: translatex(100%)
  }
  100%
  {
    top: 48.65vh;

    transform: translatex(-100%)
  }
}
}


`;
