  font-family: 'Changa', sans-serif;
  align-items: center;
  justify-content: center;
  bottom:150%;
}
.landing-inner{
  color: #fff;
  height: 100%;
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: auto;
  align-items: center;
  text-align: center;
}
`;

