import React, { Component } from 'react';
import { CardBody,CardFooter } from 'reactstrap';
import Typist from 'react-typist';
import { Link } from 'react-router-dom';
import Footer from './Footer'

export class AboutMe extends Component {
  state ={
    blinking : "|" 
  }
  blink = () => {
    this.setState({
      blinking :"_"
    })
  } 
  blinkFinish = () => {
    this.setState({
      blinking :"|"
    })
  } 
  componentDidMount () {
    setInterval(() =>{
          this.setState({
            text:"afvnjanfanf;vnjajafnjnjvsadnvsnjvnsdjkanjvsdsnvjdsnanjsadvnjsadkvnsdajkvnjsdakvnsa;jdvns;avn;savd;sa;v;sadv;sdanvsdnavjksdnavjksandvjkdbsavkjsdba;kvbsda;kvbsa;kvb;skadvbk;sdbvk;sbvk;sdbv;dsbkv;ksa;vkdsbav'saouvba'osvbao'svub'savbsabvu;saovub;savubsavubsaudvbsua;idvb;uasiuvbas;iuvbsa;ivubasbvuisvbfiyhvbreubeuvberybvreybveyrbvyuerb"
          });
      })
  }
  render(){
  return (
      <div>
    <div>     
      <div id="zir">

          <Typist  
          className="textCard"
          stdTypingDelay="100" 
          startDelay="2500"
          cursor={{
            show: true,
            blink: true,
            element: this.state.blinking,
            hideWhenDone: true,
            hideWhenDoneDelay: '1000',
          }}
          onCharacterTyped={this.blink}
          onLineTyped={this.blinkFinish}
          onTypingDone={this.blinkFinish}
          >
              {this.state.text}
            <div style={{fontSize:"4rem"}}>
            Its Me</div><br/>


          </Typist>

      </div>

    </div>
    <Footer />
          </div>
    )
  }
}
export default AboutMe;
