import React, { Component } from 'react'
import Tools from './tools'
import Contacts from './contacts'
import Hoss from './Hoss'
import { Switch, Route } from 'react-router-dom';
import { MDBBtn, MDBCollapse} from 'mdbreact';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, 
} from 'reactstrap';
import {Link} from 'react-router-dom'

export class Main extends Component {
  constructor(props)  {
      super(props);
      this.state = {
          collapseID: ""};
            }
  toggleCollapse = collapseID => () => {
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  }

 render() {
   const Home= () => {
return(
  <div>
    <Card id="card">
    <Link to="/contacts"><span>contacts</span></Link>
    <CardImg top id="cardimage" width="100%" src="../assets/hossik.jpeg" alt="hossik" className='align-self-center '/>
    <CardBody>
      <CardTitle className='row justify-content-center'>Hello, My Name is Hossik</CardTitle>
      <CardText id="cardtext">I am a Designer / Front End Developer living in Kyiv, Ukraine.
      </CardText>
    </CardBody>
  </Card>
  </div>
  )}
  return (
    <div className="header col-12 col-md-10 ">

    <Hoss />
    <MDBBtn
      className="btn btn-dark btn-circle btn-xl"
      onClick={this.toggleCollapse("basicCollapse")}>
      <i class="fa fa-question"></i>    </MDBBtn>
      <MDBCollapse id="basicCollapse" isOpen={this.state.collapseID}>
      <div>


      <Card id="card">
    <Link to="/contacts"><span>contacts</span></Link>
  <Switch>
  <Route path="/" component={Home} />
  <Route path="/contacts" component={Contacts} />
  <Route path="/tools" component={Tools} />        
  </Switch>
  </Card>
        </div>
      </MDBCollapse>
      </div>
  )}
}

export default Main
