import React, { Component } from 'react'
import { Switch, Route, Redirect } from 'react-router-dom';
import { MDBBtn} from 'mdbreact';
import { Card} from 'reactstrap';
import { withRouter} from 'react-router-dom';
import { Contacts } from './ContactComponent';
import { Home } from './HomeComponent';
import { Tools } from './ToolsComponent'
import { TransitionGroup, CSSTransition }from 'react-transition-group';
import { Test } from './TestComponent';
import Header from './Header';
import AboutMe from './AboutMe';
import Footer from './Footer';
import { ExternalLink } from 'react-external-link';
class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapseID: "",
      highlightedHobby: false,
      tbn:false

    };
  };

  listSwitch = () => {
    this.setState(state => ({
      highlightedHobby: !state.highlightedHobby
    }));
  };
  toggleCollapse = collapseID => () => {
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  }
  remove = () => {
    clearInterval(this.intervalID)
  }
  componentDidMount() {

            this.intervalID = setInterval(() => {
            this.setState(state =>({
                tbn : !state.tbn
            }));
            }, 8500);

            setInterval (this.remove, 10000)

        }

  render() {

  return (

  <React.Fragment className="container">
    <CSSTransition
          in={this.props.player}
          timeout={300}
          classNames="alertup"
          unmountOnExit
          appear     
        >
          <div id="controler">
          <MDBBtn  id="play" color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}
        onClick={this.props.playing}>{this.props.play ? <i id="hossik" class="fas fa-play-circle"></i>:<i id="nohossik" class="far fa-stop-circle"></i>}
          </MDBBtn> { }
        <CSSTransition
          in={this.props.play}
          timeout={300}
          classNames="helpplayer"
          unmountOnExit
          appear     
        >
           <div><MDBBtn id="back" color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}onClick={this.props.switchImage}><i id="hossik"  class="fas fa-forward"></i></MDBBtn>
        <MDBBtn  color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}onClick={this.props.muting}>{this.props.muted?<i id="nohossik" class="fas fa-volume-up"></i>:<i id="hossik"  class="fas fa-volume-mute"></i>}</MDBBtn>
        <MDBBtn  color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}onClick={this.props.pausing}>{this.props.pause?<i id="nohossik" class="fas fa-pause-circle"></i>:<i id="hossik"  class="fas fa-play-circle"></i>}</MDBBtn> </div>
        </CSSTransition>
       </div>
        </CSSTransition>
        <CSSTransition
          in={this.props.helpplayer}
          timeout={300}
          classNames="helpplayer"
          unmountOnExit
          appear     
        >
        <div id="help" style={{color: "white" , display : this.props.play ?  "none"  : "block" }}>
          <span><i class="fas fa-angle-double-up"></i></span>
          <p > stop video background  </p>
        </div>
        </CSSTransition>


    <ExternalLink   href="https://www.facebook.com/Hossikz">
      <button style={{background:"red", width:"8vmax",height:"10vmax" ,marginTop:"30vh" ,marginLeft:"4vw" ,position:"absolute",transform: "rotate(-35deg)",opacity:"0"}} >facebook</button>
      </ExternalLink>
    {this.state.tbn ? <MDBBtn className= " btn  btn-circle btn-xl" color="elegant"
      onClick={this.toggleCollapse("basicCollapse")}>
      <i> <Test
      isOpen={this.state.collapseID}/></i>    
    </MDBBtn> : <div></div>}

    <CSSTransition
          in={this.state.collapseID}
          timeout={300}
          classNames="list-transition"
          unmountOnExit
          appear
          onEntered={this.listSwitch}
          onExit={this.listSwitch}
        >

    <div className=" list-body"  >

        <Card  id="card" >
          <Header id="head"/>
          <div >
          <TransitionGroup  >
            <CSSTransition  key={this.props.location.pathname} classNames="fade" timeout={{ enter: 300, exit: 300 }}>
            <section className="route-section">
          <Switch location={this.props.location}>
            <Route  path="/home" component={Home} />
            <Route path="/aboutMe" component={AboutMe} />
            <Route path="/tools" component={Tools} />
            <Route path="/contacts" component={Contacts} />
            <Redirect to="/home" />
          </Switch>

          </section>
          </CSSTransition>
          </TransitionGroup>
          </div>
          <div className=" footerbox">
          <Footer/>
          </div>
        </Card>
      </div>

    </CSSTransition>

  </React.Fragment>
    )
  }
}

export default withRouter(Main);
