import styled from 'styled-components';
export const StyledIntro = styled.div`
height: 100vh;
width: 100%;
background : black;
position: fixed;
.linetext{
    margin-top: 33vh;
    font-family: 'Changa', sans-serif;
    align-items: center;
    justify-content: center;
    text-align: center;
    font-size:12em;
}
#up{
    font-size:13vmin ;
    color: rgb(233, 25, 25);
    font-weight: bold;
    z-index: -1;
}
#down{ 
    font-size: 14.5vmin;
    color: rgb(255, 220, 0);
    font-weight: bold;
    z-index: -1;
}

.line{
   color:white; 
   width: 100%;
   diplay:flex;
   white-space: nowrap;
   position: absolute;
  top: 50vh;

  font-weight:900;
  transform: translate(-50%, -50%);
  -ms-transform: translate(-50%, -50%);
  animation: animateline 4s linear;
}
@keyframes animateline
{
  0%
  {
    top: 48.65vh;

    transform: translatex(100%)
  }
  100%
  {
    top: 48.65vh;

    transform: translatex(-100%)
  }
}
}


`;
