import React, { Component } from 'react'
import Info from './Info'
import Header from './Header'
import Hoss from './Hoss'
import Home from './Home'
import Face from './Face'
import { Switch, Route, Redirect } from 'react-router-dom';
import { MDBCard,MDBCardTitle, MDBCardText,MDBCardBody} from 'mdbreact';

export class Main extends Component {
  constructor(props)  {
      super(props);
      this.state = {
        isButtonOpen:false
      };
      this.toggleButton = this.toggleButton.bind(this);
  }
  toggleButton(){
    this.setState({
      isButtonOpen: !this.state.isButtonOpen
    });
  }

render() {
     const Form = () =>{ 
      return(
     <div className=" col-12 col-md-10 ">

       <button type="button" onClick={this.toggleButton} class="btn btn-dark btn-circle btn-xl"  data-toggle="collapse" data-target="#form"
    aria-expanded="false" aria-controls="form" > form</button>


     <MDBCard style={{ width: "22rem" }} 
     id= 'form' isOpen={this.state.isButtonOpen} toggle={this.toggleButton} >

     <MDBCardBody>
    <MDBCardTitle  className='row justify-content-center'>Hoss</MDBCardTitle>
    <MDBCardText style={{ fontSize: "2rem" }} className='row justify-content-center' >
    I am a web developer
    </MDBCardText>
    </MDBCardBody>
    </MDBCard>
    </div>)

    }

  return (
    <div >
      <Form />
      <Header />
      <Switch>
      <Route  path="/info" component={Info} />
      <Route path="/home" component={Home} />
      <Route path= "/face" component={Face} />
      <Redirect to="/hoss" component={Hoss} />

      </Switch>
      <Hoss />
    </div>
  )
}
}

export default Main
