import { StyledCover } from './Cover.styled';
import Main from '../Main';
import React, { Component } from 'react'

export class Cover extends Component {

    constructor(props) {
        super(props);
        this.switchImage = this.switchImage.bind(this);
        this.state = {
          currentImage: 0,
          images: [
  '../assets/photo1.png', 
  '../assets/photo2.png', 
  '../assets/photo3.png', 
