   '../assets/photo50.png', 
   '../assets/photo51.png', 
   '../assets/photo52.png', 
   '../assets/photo53.png', 
   '../assets/photo54.png', 
   '../assets/photo55.png', 
   '../assets/photo56.png', 
   '../assets/photo57.png',
   '../assets/photo58.png', 
   '../assets/photo59.png', 
          ]
        };
      }

      switchImage() {
        if (this.state.currentImage < this.state.images.length - 1) {
          this.setState({
            currentImage: this.state.currentImage + 1
          });
        } else {
          this.setState({
            currentImage: 0
          });
        }
        return this.currentImage;
      }

      componentDidMount() {
        setInterval(this.switchImage, 1000);
      }


    render() {
        return (

            <StyledCover>
        <img src={this.state.images[this.state.currentImage]} alt="img" />
        <div className ="landing-inner">
        <div className='inner'>
            <Main className="hoss"/>
        </div>
        </div>
        </StyledCover>

        )
    }
}

export default Cover











