import React, { Component } from 'react'
import Info from './Info'
import Hoss from './Hoss'
import Face from './Face'
import { Switch, Route, Redirect } from 'react-router-dom';
import { MDBBtn, MDBCollapse, MDBCard,MDBCardTitle, MDBCardText,MDBCardBody} from 'mdbreact';

export class Main extends Component {
  constructor(props)  {
      super(props);
      this.state = {
          collapseID: ""};
            }
  toggleCollapse = collapseID => () => {
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  }

render() {
     const Header = () =>{ 
      return(
     <div className="header col-12 col-md-10 ">
    <MDBBtn
      className="btn btn-dark btn-circle btn-xl"
      onClick={this.toggleCollapse("basicCollapse")}>
<i class="fa fa-question"></i>    </MDBBtn>
    <MDBCollapse id="basicCollapse" isOpen={this.state.collapseID}>
    <MDBCard style={{ width: "22rem" }}>
    <MDBCardBody>
      <MDBCardTitle  className='row justify-content-center'>Hoss</MDBCardTitle>
      <MDBCardText style={{ fontSize: "2rem" }} className='row justify-content-center' >
      I am a web developer
      </MDBCardText>
      <Link to="/home"><MDBBtn className= 'tn btn-circle btn-xl btn-fb' tag="a" size="lg" floating gradient="morpheus-den" >
    <i class="fa fa-home fa-lg"></i>
  </MDBBtn></Link>
  <MDBBtn className= 'tn btn-circle btn-xl' tag="a" floating gradient="blue">
  <i class="fa fa-info fa-lg"></i>
  </MDBBtn>
  <MDBBtn className= 'tn btn-circle btn-xl' tag="a" size="sm" floating gradient="aqua">
  <i class="fa fa-address-card fa-lg"></i>
  </MDBBtn>
    </MDBCardBody>
  </MDBCard>
    </MDBCollapse>
    </div>
    )}
  return (
    <div >
      <Header />
      <Switch>
      <Route  path="/info" component={Info} />
      <Route path= "/face" component={Face} />
      <Redirect to="/header" component={Header} />

      </Switch>
      <Hoss />
    </div>
  )
}
}

export default Main
