import React, { Component } from 'react';
import {Card,CardImg,CardBody,CardTitle} from "reactstrap";

export class Info extends Component {
    renderInfo(info) {
          if (info != null)
          return (
            <div className=" col-12 col-md-5 m-1">
            <Card >
            <CardImg src='../assets/hoss.jpeg' alt='photo' />
            <CardBody>
              <CardTitle className='row justify-content-center'>I am a web developer</CardTitle>
            </CardBody>
          </Card>
          </div>
          )
        }
    render() {
        return (
        <div className='align-self-center'>
            {this.renderInfo(this.props.click)}
        </div> 
        )
    }
}

export default Info
