import { createGlobalStyle } from 'styled-components'

export const GlobalStyle = createGlobalStyle`
  body {

    // background : url(${({ theme }) => theme.color_1})  center center fixed







    transition: opacity 1s ease-in-out;

    animation-name: blinker;
    animation-direction: alternate;

    animation-duration: 200s;
    animation-timing-function: ease-in-out;
    animation-iteration-count: infinite;
    @keyframes blinker {
      5% { background : url(${({ theme }) => theme.color_1})no-repeat center fixed cover}
      15% { background : url(${({ theme }) => theme.color_3}) no-repeat center fixed cover}
      25% { background : url(${({ theme }) => theme.color_5}) no-repeat center fixed cover}
      35% { background : url(${({ theme }) => theme.color_7})no-repeat center fixed cover }
      45% { background : url(${({ theme }) => theme.color_9})no-repeat center fixed cover }
      55% { background : url(${({ theme }) => theme.color_11}) no-repeat center fixed cover}
      65% { background : url(${({ theme }) => theme.color_13}) no-repeat center fixed cover}
      70% { background : url(${({ theme }) => theme.color_1}) no-repeat center fixed cover}
      75% { background : url(${({ theme }) => theme.color_3}) no-repeat center fixed cover}
      80% { background : url(${({ theme }) => theme.color_5}) no-repeat center fixed cover}
      85% { background : url(${({ theme }) => theme.color_7})  no-repeat center fixed cover}
      90% { background : url(${({ theme }) => theme.color_9})no-repeat center fixed cover }
      95% { background : url(${({ theme }) => theme.color_11}) no-repeat center fixed cover}
      100% { background : url(${({ theme }) => theme.color_13}) no-repeat center fixed cover}


    }
  }

`
