import { StyledCover } from './Cover.styled';
import Main from '../Main';
import Player from '../Player';
import React from 'react';

import ReactTextTransition, { presets } from "react-text-transition";
import {  CSSTransition }from 'react-transition-group';



import { BannerImage } from './Banner';

const hossik = ["HOSSIK PAGE"," "]
const welcome = ["WELCOME TO"," "]

 const InnerCover = (props) => {

    return(
        <StyledCover>

    <div className="light-overlay">





          <BannerImage />
          <div className ="play">
            <Player  

            play={props.play}
            playing={props.playing}
            player = {props.player}
            helpplayer={props.helpplayer}
            muted={props.muted}
            pause={props.pause}
            images={props.images}
            pausing={props.pausing}
            muting={props.muting}
           />
            </div>

          <div className='inner'>

            <Main className="hoss"  />

        <CSSTransition
          in={props.titletime}
          timeout={300}
          classNames="alertup"
          unmountOnExit
          appear     
        >
              <section  id= "up" className="linetext">
              <ReactTextTransition
                 text= { welcome[props.textIndex % welcome.length] }
                 springConfig={ presets.molasses
                 }
                 delay={400}
                 className="big"
                 direction="up"
                 inline
              />
              </section>
              </CSSTransition>
              <CSSTransition
          in={props.titletime}
          timeout={300}
          classNames="alertdown"
          unmountOnExit
          appear     
        >
              <section id="down" className="linetext">
              <ReactTextTransition
                 text= { hossik[props.textIndex % hossik.length] }
                 springConfig={ presets.molasses
                 }
                 delay={400}
                 className="big"
                 direction="down"
                 inline
              />         
              </section>
              </CSSTransition> 

          </div>
          </div>



    </StyledCover>
    )

  }

export default InnerCover
