import React, { Component } from 'react';
import Typist from 'react-typist';
import './text.scss';
import { Link } from 'react-router-dom';
import Filter from 'react-css-filter'

const Sapp = () => (
  <Filter effects={{'drop-shadow': '2px 2px 3px #08C', sepia: '60%'}}><Typist 
  className="TypistExample-header"
  avgTypingDelay={40}
  startDelay={2000}
  onTypingDone={this.onHeaderTyped}>
    Its Me Typist will animate any text 
</Typist></Filter>
)
export class Home extends Component {
  state ={
    blinking : "|" ,
    renderMsg: false
  }
  blink = () => {
    this.setState({
      blinking :"_"
    })
  } 
  blinkFinish = () => {
    this.setState({
      blinking :"|"
    })
  } 
  onHeaderTyped = () => {
    this.setState({ renderMsg: true });
  }
  render(){
  return (
      <div> 
           <Sapp />


        <div >
          {this.state.renderMsg ? (
            <Typist
              className="TypistExample-message"
              cursor={{ hideWhenDone: true }}
            >
              * Easy to style
              <Typist.Delay ms={1250} />
              <br />
              * Easy to customize
              <Typist.Delay ms={1250} />
              <br />
              * Easy to use backp<Typist.Delay ms={500} />sace
              <Typist.Backspace count={5} delay={1000} />
              <Typist.Delay ms={750} />
              space
              <Typist.Delay ms={1250} /> 
              <br />
              <span >for more information < Link style={{color:'#FFDC00'}}>*About me <span className="flash" to={"/aboutMe"} ><img id="man" alt="hossik" src="https://raw.githubusercontent.com/Hossik/Profilio/master/public/assets/hossik.jpeg" /></span></Link></span>

              {''}
            </Typist>
          ) : null }
        </div>

      </div>
    )
  }
}
export default Home;
