import { createGlobalStyle } from 'styled-components'

export const GlobalStyle = createGlobalStyle`
  body {

background:yellow;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  height: 1800px;


  -webkit-backface-visibility: hidden;
	-webkit-transform: scale(1);
  -webkit-transition: background-image 0.2s ease-in-out;
  -webkit-transition: all 4s ease, z-index 1ms;
       -moz-transition: all 4s ease, z-index 1ms;


        -ms-transition: all 4s ease, z-index 1ms;
        -ms-animation-duration: 1s;

         -o-transition: all 4s ease, z-index 1ms;
         -0-animation-duration: 1s;

            transition: all 4s ease, z-index 1ms;
            animation-duration: 10s;
            -moz-animation-duration: 1s;
  transition: background-image 0.2s ease-in-out;


    animation-name: blinker;


    animation-timing-function: ease-in-out;
    animation-iteration-count: infinite;
    @keyframes blinker {
      0% {background-image : url(${({ theme }) => theme.color_1})}
      5% { background-image : url(${({ theme }) => theme.color_1})}
      10% { background-image : url(${({ theme }) => theme.color_1})}

      15% { background-image : url(${({ theme }) => theme.color_2}) }
      20% { background-image : url(${({ theme }) => theme.color_2})}

      25% { background-image : url(${({ theme }) => theme.color_2}) }
      30% { background-image : url(${({ theme }) => theme.color_3})}

      35% { background-image : url(${({ theme }) => theme.color_3}) }
      40% { background-image : url(${({ theme }) => theme.color_3})}

      45% { background-image : url(${({ theme }) => theme.color_4})  }
      50% { background-image : url(${({ theme }) => theme.color_4})}

      55% { background-image : url(${({ theme }) => theme.color_4})  }
      60% { background-image : url(${({ theme }) => theme.color_5})}

      65% { background-image : url(${({ theme }) => theme.color_5})  }

      70% { background-image : url(${({ theme }) => theme.color_5})  }
      75% { background-image : url(${({ theme }) => theme.color_6})  }
      80% { background-image : url(${({ theme }) => theme.color_6})  }
      85% { background-image : url(${({ theme }) => theme.color_6})   }
      90% { background-image : url(${({ theme }) => theme.color_7})  }
      95% { background-image : url(${({ theme }) => theme.color_7})  }
      100% { background-image : url(${({ theme }) => theme.color_7})  }


    }
  }

`
