import React, { Component } from 'react'
import { Switch, Route, Redirect } from 'react-router-dom';
import { MDBBtn, MDBCollapse } from 'mdbreact';
import { Card} from 'reactstrap';
import { withRouter} from 'react-router-dom';
import { Contacts } from './ContactComponent';
import { Home } from './HomeComponent';
import { Tools } from './ToolsComponent'
import { TransitionGroup, CSSTransition }from 'react-transition-group';
import { Test } from './TestComponent';
import Header from './Header';
import AboutMe from './AboutMe';
import Footer from './Footer';

class Main extends Component {
  state = {
    showList: true,
    highlightedHobby: false
  };

  switch = () => {
    this.setState(prevState => ({
      showList: !prevState.showList
    }));
  };

  listSwitch = () => {
    this.setState(state => ({
      highlightedHobby: !state.highlightedHobby
    }));
  };


  render() {

  return (



      <div className="container">
        <button className="display" onClick={this.switch}>
          Obinna
        </button>
        <CSSTransition
          in={this.state.showList}
          timeout={400}
          classNames="list-transition"
          unmountOnExit
          appear
          onEntered={this.listSwitch}
          onExit={this.listSwitch}
        >

    <div className="container"  >
      <div className="row " >    
        <Card id="card" >
          <Header id="head"/>
          <div >
          <TransitionGroup  >
            <CSSTransition  key={this.props.location.key} classNames="fade" timeout={{ enter: 300, exit: 300 }}>
            <section className="route-section">
          <Switch location={this.props.location}>
            <Route  path="/home" component={() => <Home isOpen={this.state.collapseID}/>} />
            <Route path="/aboutMe" component={AboutMe} />
            <Route path="/tools" component={Tools} />
            <Route path="/contacts" component={Contacts} />
            <Redirect to="/home" />
          </Switch>

          </section>
          </CSSTransition>
          </TransitionGroup>
          </div>
          <Footer />
        </Card>
      </div>
    </div>

    </CSSTransition>
      </div>
    )
  }
}

export default withRouter(Main);
