import { createGlobalStyle } from 'styled-components'

export const GlobalStyle = createGlobalStyle`
  body {


    background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  position: relative;





    transition: opacity 1s ease-in-out;

    animation-name: blinker;

    animation-duration: 50s;
    animation-timing-function: ease-in-out;
    animation-iteration-count: infinite;
    @keyframes blinker {
      5% { background-image : url("${({ theme }) => theme.color_1}")}
      15% { background-image : url("${({ theme }) => theme.color_3}") }
      25% { background-image : url("${({ theme }) => theme.color_5}") }
      35% { background-image : url("${({ theme }) => theme.color_7}") }
      45% { background-image : url(${({ theme }) => theme.color_9})  }
      55% { background-image : url(${({ theme }) => theme.color_11})  }
      65% { background-image : url(${({ theme }) => theme.color_13})  }
      70% { background-image : url(${({ theme }) => theme.color_1})  }
      75% { background-image : url(${({ theme }) => theme.color_3})  }
      80% { background-image : url(${({ theme }) => theme.color_5})  }
      85% { background-image : url(${({ theme }) => theme.color_7})   }
      90% { background-image : url(${({ theme }) => theme.color_9})  }
      95% { background-image : url(${({ theme }) => theme.color_11})  }
      100% { background-image : url(${({ theme }) => theme.color_13})  }


    }
  }

`
