import React, { Component } from 'react'
import Contacts from './contacts'
import Hoss from './Hoss'
import { Switch, Route,Redirect,Link } from 'react-router-dom';
import { MDBBtn, MDBCollapse} from 'mdbreact';
import {Card, CardImg, CardText, CardBody,CardTitle,CardSubtitle} from 'reactstrap';

export class Main extends Component {
  constructor(props)  {
      super(props);
      this.state = {
          collapseID: ""};
            }
  toggleCollapse = collapseID => () => {
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  }

 render() {
   const Home= () => {
return(
  <div>

    <CardImg top id="cardimage" width="100%" src="../assets/hossik.jpeg" alt="hossik" />
    <CardBody>
    <CardSubtitle className='row justify-content-center'>Hello, My Name is Hossik</CardSubtitle>

      <CardText id="cardtext">I am a Designer / Front End Developer living in Kyiv, Ukraine.
      </CardText>
    </CardBody>

  </div>

  )}
  const Tools= () => {
    return(
      <div>

        <CardImg top id="cardimage" width="100%" src="../assets/react.png" alt="hossik" />
        <CardBody>
        <CardSubtitle className='row justify-content-center'>Hello, My Name is Hossik</CardSubtitle>

          <CardText id="cardtext">I am a Designer / Front End Developer living in Kyiv, Ukraine.
          </CardText>
        </CardBody>

      </div>

      )}
  return (
    <div className="header col-12 col-md-10 ">

    <Hoss />
    <MDBBtn
      className="btn btn-dark btn-circle btn-xl"
      onClick={this.toggleCollapse("basicCollapse")}>
      <i class="fa fa-question"></i>    </MDBBtn>
      <MDBCollapse id="basicCollapse" isOpen={this.state.collapseID}>
      <div>


  <Card id="card">
  <CardTitle className='row justify-content-center'>
    <Link className="links" to="/home"><span>home</span></Link>
    <Link className="links" to="/contacts"><span>contacts</span></Link>
    <Link className="links" to="/tools"><span>tools</span></Link>

    </CardTitle>
  <Switch>
  <Route path="/home" component={Home} />
  <Route path="/contacts" component={Contacts} />
  <Route path="/tools" component={Tools} />  
  <Redirect to="/home" />      
  </Switch>
  </Card>
        </div>
      </MDBCollapse>
      </div>
  )}
}

export default Main
