import './App.css';
import { theme } from './theme';

export class App extends Component  {
  constructor(props) {
    super(props);
    this.state = {
      intro:false
  }
  }
  remove = () => {
    clearInterval(this.intervalID)
  }
  componentDidMount() {

    this.intervalID = setInterval(() => {
      this.setState(state => ({
        intro : !state.intro
      }));
    }, 14000);
    setInterval (this.remove, 15000)
  }

render(){

  return (
  <React.Fragment>

  <HashRouter >
  <GlobalStyles />
   <section className="landing" >
    <Cover />
    </section>
  </HashRouter>




  </React.Fragment>
  )

}
}

export default App;
