import React, { Component } from 'react'
import { Switch, Route, Redirect, Link } from 'react-router-dom';
import { MDBBtn, MDBCollapse } from 'mdbreact';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle }
from 'reactstrap';
import {BrowserRouter, withRouter} from 'react-router-dom';
import { Contacts } from './Contacts'
import { TransitionGroup, CSSTransition }from 'react-transition-group'
import ReactTextTransition, { presets } from "react-text-transition";
const randomNumber = () => Math.floor(Math.random() * 9999999999 + 10000000000);

const texts = ["Click", "or", "Die"]
const paragraphs = [
  "Quis commodo velit quis cupidatat non amet aliqua sint veniam labore. Culpa ea laboris consequat mollit proident Lorem cupidatat officia fugiat. Tempor sunt non ullamco irure in proident. Laboris minim esse qui non id et anim commodo. Esse id do irure ut eiusmod aliqua irure dolor in est ullamco culpa enim. ",
  "Quis deserunt tempor consectetur id ea. Aute sunt eu minim nostrud officia excepteur duis et tempor do dolor culpa. Cillum nostrud quis magna consequat.",
  "Aute in id ullamco laboris aute tempor magna dolore ad laborum.",
  "Exercitation ullamco qui occaecat dolore ex cillum exercitation commodo magna Lorem in laborum do do. Laboris ad mollit adipisicing ad occaecat fugiat tempor commodo irure magna culpa. Culpa eiusmod nisi sit non ipsum."
];

class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapseID: "",
      number: randomNumber(),
    textIndex: 0,
    textFastIndex: 0,
    paragraphIndex: 0
    };
  }

    componentDidMount() {
      setInterval(() => {
        this.setState({
          number: randomNumber(),
          textIndex: this.state.textIndex + 1,
          paragraphIndex: this.state.paragraphIndex + 1
        });
      }, 4000);
      setInterval(() => {
        this.setState({
          textFastIndex: this.state.textFastIndex + 1
        });
      }, 150);
    }

  toggleCollapse = collapseID => () => {
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  }

  render() {

    const Home = () => {
      return (
        <div>
          <CardImg top id="cardimage" width="100%" src="../assets/hossik.jpeg" alt="hossik" />
          <CardBody>
            <CardSubtitle className='row justify-content-center'>Hello, My Name is Hossik</CardSubtitle>
            <CardText id="cardtext">I am a Designer / Front End Developer living in Kyiv, Ukraine.
          </CardText>
          </CardBody>
        </div>
      )
    }
    const Tools = () => {
      return (
        <div>
          <CardImg top id="cardimage" width="100%" src="../assets/react.png" alt="hossik" />
          <CardBody>
            <CardSubtitle className='row justify-content-center'>Hello, My Name is Hossik</CardSubtitle>
            <CardText id="cardtext">I am a Designer / Front End Developer living in Kyiv, Ukraine.
          </CardText>
          </CardBody>
        </div>
      )
    }


  return (

    <React.Fragment className="header col-12 col-md-10 ">
      <div className="container">
              <div className="row h-100 justify-content-center">
                  <div className='align-self-center my-auto'>
                  <section>

          <section className="inline">
            <ReactTextTransition
              text={texts[this.state.textIndex % texts.length]}
              spring={presets.gentle}
              className="big"
              delay={300}
              inline
            />

          </section>
        </section>

                  </div>
              </div>
          </div>
      <MDBBtn
        className="btn btn-dark btn-circle btn-xl"
        onClick={this.toggleCollapse("basicCollapse")}>
        <i class="fa fa-cog fa-spin fa-3x fa-fw"></i>    </MDBBtn>
      <MDBCollapse id="basicCollapse" isOpen={this.state.collapseID}>
        <div>
          <Card id="card" className="col-12 col-md-10" style={{width:'120rem' ,height:"80rem"}}>
            <CardTitle className='row justify-content-center'>
              <Link className="links" to="/home"><span>home</span></Link>      
              <Link className="links" to="/tools"><span>tools</span></Link><Link className="links" to="/contacts"><span>contacts</span></Link>
            </CardTitle>
            <TransitionGroup >
              <CSSTransition key={this.props.location.key} classNames="fade" timeout={{ enter: 300, exit: 300 }}>
              <section className="route-section">
            <Switch location={this.props.location}>
              <Route path="/home" component={Home} />
              <Route path="/tools" component={Tools} />
              <BrowserRouter>
                <div className='hoss '>
                  <Contacts />
                </div>
              </BrowserRouter>        
              <Redirect to="/home" />
            </Switch>
            </section>
            </CSSTransition>
            </TransitionGroup>
          </Card>
        </div>
      </MDBCollapse>
    </React.Fragment>
    )
  }
}

export default withRouter(Main);
