import React from 'react'

export default function Player() {
    return (
        <div>
           <CSSTransition
          in={this.props.player}
          timeout={300}
          classNames="alertup"
          unmountOnExit
          appear     
        >
          <div id="controler">
          <MDBBtn  id="play" color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}
        onClick={this.props.playing}>{this.props.play ? <i id="hossik" class="fas fa-play-circle"></i>:<i id="nohossik" class="far fa-stop-circle"></i>}
          </MDBBtn> { }
        <CSSTransition
          in={this.props.play}
          timeout={300}
          classNames="helpplayer"
          unmountOnExit
          appear     
        >
           <div><MDBBtn id="back" color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}onClick={this.props.switchImage}><i id="hossik"  class="fas fa-forward"></i></MDBBtn>
        <MDBBtn  color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}onClick={this.props.muting}>{this.props.muted?<i id="nohossik" class="fas fa-volume-up"></i>:<i id="hossik"  class="fas fa-volume-mute"></i>}</MDBBtn>
        <MDBBtn  color="elegant" style={{textAlign: 'center',margin: 'auto' ,padding:"auto"}}onClick={this.props.pausing}>{this.props.pause?<i id="nohossik" class="fas fa-pause-circle"></i>:<i id="hossik"  class="fas fa-play-circle"></i>}</MDBBtn> </div>
        </CSSTransition>
       </div>
        </CSSTransition>
        <CSSTransition
          in={this.props.helpplayer}
          timeout={300}
          classNames="helpplayer"
          unmountOnExit
          appear     
        >
        <div id="help" style={{color: "white" , display : this.props.play ?  "none"  : "block" }}>
          <span><i class="fas fa-angle-double-up"></i></span>
          <p > stop video background  </p>
        </div>
        </CSSTransition>

        </div>
    )
}
