import React, { Component } from 'react';
import Info from './Info'
export class Header extends Component {
    constructor(props) {	 
        super(props);
        selectedComments:null
        this.state = {	
            click: null	
        }	
    }	

    onclick =(click) =>{
        this.setState({click: click})
    }
    render() {
        const button = () => {
            return(
            <div className="header">
                <button type="button" class="btn btn-dark btn-circle btn-xl" onClick= {() =>this.onclick}><i class="fa fa-question"></i></button> 
            </div> 
            )
        }
        return (
            <div >
            {button}

            <Info click={this.state.click} />
            </div>
        )
    }
}

export default Header
