import React, { Component } from 'react'
import classes from './BackgroundVideo.module.css';
import Main from './Main';
import { BrowserRouter } from 'react-router-dom';
import ReactTextTransition, { presets } from "react-text-transition";
const randomNumber = () => Math.floor(Math.random() * 9999999999 + 10000000000);
const videoSource = "../assets/videoCover.mp4"
const texts = ["Click", "or", "Die"]
class BackgroundVideo extends Component {
    state = {
        collapseID: "",
        number: randomNumber(),
      textIndex: 0,
      textFastIndex: 0,
      paragraphIndex: 0
      };


      componentDidMount() {
        setInterval(() => {
          this.setState({
            number: randomNumber(),
            textIndex: this.state.textIndex + 1,
            paragraphIndex: this.state.paragraphIndex + 1
          });
        }, 4000);
        setInterval(() => {
          this.setState({
            textFastIndex: this.state.textFastIndex + 1
          });
        }, 150);
      }

      render() {

    return (
        <div className={classes.Container} >
            <video autoPlay="autoplay" loop="loop" muted className={classes.Video} >
                <source src={videoSource} type="video/mp4" />
                Your browser does not support the video tag.
            </video>

            <div className={classes.Content}>

                <BrowserRouter >
                <section>
        <section className="inline" id="hossik">
          <ReactTextTransition
            text={texts[this.state.textIndex % texts.length]}
            spring={presets.gentle}
            className="big"
            delay={300}
            inline
          />   
        </section>
      </section>
                <div className='hoss'>
                    <Main/>
                </div>
                </BrowserRouter>

            </div>
        </div>
    )
  }
}
export default BackgroundVideo
