import React from "react";
import { Wave} from "react-animated-text";

const exampleStyle = {
  display: "inline-block",
  border: "1px solid #ccc",
  marginBottom: "1em",
  padding: "2em 1em 1em 1em",
  width: "80%",
  fontSize: "1.5rem"
};

const codeDivStyle = {
  backgroundColor: "#eee",
  marginTop: "2em",
  padding: "1px 1em",
  overflow: "scroll",
  fontSize: "1rem"
};

const codeStyle = {
  textAlign: "left"
};

const buttonStyle = {
  cursor: "pointer",
  backgroundColor: "#69c",
  border: "none",
  color: "white",
  appearance: "none",
  boxShadow: "none",
  borderRadius: 0,
  fontSize: "1.0rem",
  padding: "0.2em 2em"
};

class Text extends Component {
  constructor(props) {
    super(props);

    this.state = {
      list:
        "Quis commodo velit quis cupidatat non amet aliqua sint veniam labore. Culpa ea laboris consequat mollit proident Lorem cupidatat officia fugiat. Tempor sunt non ullamco irure in proident. Laboris minim esse qui non id et anim commodo. Esse id do irure ut eiusmod aliqua irure dolor in est ullamco culpa enim."
    };
  }

  render() {
    return (
      <div style={exampleStyle}>
        <Wave
          text={this.state.list}
          effect="verticalFadeIn"
          effectChange={0}
          iterations={1}
          effectDirection="down"
          effectDuration={0.1}
        />
        <Wave
          text="_"
          effect="verticalFadeOut"
          effectChange={0}
          effectDirection="up"
          effectDuration={9}
        />

      </div>
    );
  }
}
export default Text ;
