import React, { Component } from 'react'
import Info from './Info'
import Face from './Face'
import Home from './Home'
import Hoss from './Hoss'
import { Switch, Route } from 'react-router-dom';
import { MDBBtn, MDBCollapse} from 'mdbreact';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button
} from 'reactstrap';
import {Link} from 'react-router-dom'

export class Main extends Component {
  constructor(props)  {
      super(props);
      this.state = {
          collapseID: ""};
            }
  toggleCollapse = collapseID => () => {
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  }

 render() {

  return (
    <div className="header col-12 col-md-10 ">
    <Hoss />
    <MDBBtn
      className="btn btn-dark btn-circle btn-xl"
      onClick={this.toggleCollapse("basicCollapse")}>
      <i class="fa fa-question"></i>    </MDBBtn>
      <MDBCollapse id="basicCollapse" isOpen={this.state.collapseID}>
    <Card id="card">
        <CardImg top id="cardimage" width="100%" src="../assets/hossik.jpeg" alt="hossik" className='align-self-center my-auto'/>
        <CardBody>
          <CardTitle className='align-self-center my-auto'>My Name is Hossik</CardTitle>
          <CardSubtitle>Card subtitle</CardSubtitle>
          <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
          <Switch>
        <Route path="/home" component={Home} />
        <Route path="/info" component={Info} />
        <Route path="/face" component={Face} />        
        </Switch>
          <Button>Button</Button>
        </CardBody>
      </Card>
      </MDBCollapse>
      </div>
  )}
}

export default Main
