import React, { Component } from 'react'
import Info from './Info'
import Header from './Header'
import Hoss from './Hoss'


export class Main extends Component {
    constructor(props) {	 
        super(props);
        this.state = {	
        click: null
        }	
    }	
    onclick =(click) =>{
        this.setState({click: click})
    }
    render() {
        return (
            <div>
              <Header onclick = {(click) => this.onclick (click)}/>
              <Info click={this.state.click} />
              <Hoss />
            </div>
        )
    }
}

export default Main
